module.exports = function(grunt) {
  grunt.registerTask('thorax:inspector', function() {
    this.requiresConfig('thorax.inspector');
    var config = grunt.config('thorax.inspector');
    require('../thorax-inspector.js')(config);
    if (!('background') in config) {
      config.background = true;
    }
    if (!config.background) {
      this.async();
    }
  });
};